import numpy as np


class Solution:
    def longestAwesome(self, s: str) -> int:
        all_numbers_counts = [0] * 10
        len_s = len(s)
        dp = np.zeros((len_s, 10), dtype=int)
        min_result = 0
        for i in range(len_s):
            s_child = int(s[i])
            all_numbers_counts[s_child] += 1
            if i > 0:
                dp[i] = dp[i - 1]
            dp[i][s_child] += 1
            if self.isAwesome(dp[i]):
                min_result = i + 1
        if min_result == len_s:
            return min_result
        for i in range(len_s):
            if len_s - i - 1 < min_result:
                continue
            for j in reversed(range(i, len_s)):
                sum_value = j - i
                if sum_value < min_result:
                    continue
                target = dp[j] - dp[i]
                if self.isAwesome(target):
                    min_result = sum_value
        return min_result

    def isAwesome(self, occurrences):
        return np.count_nonzero((occurrences % 2)) < 2


